import React, {useState} from 'react';
import image from './image.jpeg'


function LoginForm({login, error}) {

 const [details, setDetails] = useState({username:'' , password:''});

    const submitHandler = e =>{
        e.preventDefault();

        login(details);
    }

    const setName = (e)  => { 
        setDetails({...details, password:e.target.value})
    } 

   const setPassword = (e) => {
        setDetails({...details,username:e.target.value})
    }

    return (
        <div className="Main">
            <div className="left">
            <img src={image} width="100%" height="100vh"></img>
            </div>
            <div className="right">
            <label>{error}</label>
        <form onSubmit={submitHandler}>
            <div className="my-form">
                <h2>Login</h2>
                <div classNAME="form-body" alt="baby ele">
                    <label htmlFor="username">UserName:</label>
                    <input type="text" name="UserName" id="UserName" onChange={setPassword} value={details.username}/>
                    <label htmlFor="email">Password</label>
                    <input type= "Password" name ="Password" id="Password" onChange={setName}  value={details.password}/>
                    <p>{error}</p>
                    <button>Login</button>

                    </div>
            </div>
        </form>
        </div>
        </div>
    )
}

export default LoginForm
